VALUES (116, 'Boardgame LHT Tỷ Phú - The Game 02', 3, 4, 6, 15, 10, 10, 10, 100, 3,
        'Boardgame Tỷ phú là trò chơi về tích lũy bất động sản và thâu tóm tài sản của nhau.\n Bằng cách sử dụng tính năng trên thẻ, bạn chơi có thể thu tiền thuê đất, đổi thẻ, đòi tiền sinh nhật… để tăng tích tài sản và chiến thắng thần tốc.\n Người đầu tiên sở hữu 3 bộ đất khác màu nhau sẽ là người thắng cuộc.\n Bộ game được thiết kế rất đẹp mắt, tỉ mỉ, hướng dẫn sử dụng rõ ràng, bao hộp chắc chắn.',
        NULL, 4, 55, 127000, 3, '2021-11-30 00:00:00', '2021-12-30 00:00:00');
INSERT INTO `product`(`id`, `name`, `age`, `min_player`, `max_player`, `time_play`, `height`, `width`, `tall`, `weight`,
                      `producer_id`, `desc`, `img_display`, `category_id`, `inventory`, `price`, `discount_id`,
                      `create_at`, `update_at`)
VALUES (117, 'Bộ Bài Tarot The Smith-Waite Tarot Centennial Edition Pamela', 10, 4, 6, 15, 10, 10, 10, 100, 4,
        'The Smith-Waite Tarot Centennial Edition là tựa bài kỷ niệm 100 năm ra mắt Rider-Waite Tarot, với thiết kế hình ảnh sử dụng lại bản vẽ gốc của họa sĩ Pamela Colman Smith (Pixie) năm 1909. Bộ bài được đặt theo tên nữ họa sĩ này để tri ân di sản bà để lại cho thế giới Tarot – hệ thống hình ảnh đã trở thành chuẩn mực của hầu hết mọi tựa bài Tarot sau này – sau một thời gian dài bị lãng quên khi mọi người có khuynh hướng chỉ chú ý đến tên của Waite – tác giả hệ thống biểu tượng của Rider-Waite đồng thời là người hướng dẫn Pixie hoàn thiện hình ảnh 78 lá bài theo tư tưởng của ông, và Rider – người đã cung cấp kinh phí và điều kiện để phát hành bộ bài. Hình ảnh 78 lá bài của bản vẽ gốc vận dụng nhiều tông màu lặng (muted), ngả tối và mang màu sắc cổ điển rõ nét.\n \n Phiên bản đặc biệt bao gồm bộ bài, các postcard, một tấm hình chân dung của Pixie, một tấm gấp trải bài bằng giấy có in sẵn ba trải bài, một túi đựng bài vải organza, và hai cuốn sách: Pictorial Key to the Tarot của tác giả Arthur Edward Waite và The Artwork & Times of Pamela Colman Smith của Stuart Kaplan. Trong khi đó phiên bản rút gọn bao gồm bộ bài, các postcard và sách booklet rút gọn.',
        NULL, 3, 214, 179000, 3, '2021-04-30 00:00:00', '2021-05-19 00:00:00');
